mapper.insert:
	INSERT INTO schedule_tbl(
	title, description, write_user, target_user, 
	test_start_date, test_end_date, 
	event_type, backgroundColor, all_day
    )
    VALUES (
    #{m.title}, #{m.description}, #{m.write_user}, #{m.target_user}, 
    #{m.start}, #{m.end}, 
    #{m.event_type}, #{m.backgroundColor}, #{m.all_day}
    )

mapper.update:
UPDATE schedule_tbl SET title = #{m.title}, description = #{m.description}, write_user = #{m.write_user}, target_user = #{m.target_user}, test_start_date = #{m.start},
test_end_date = #{m.end},
event_type = #{m.event_type}, backgroundColor = #{m.backgroundColor}, all_day = #{m.all_day} WHERE id = #{m.id}
